const input =
  "";

const d: Record<string, number> = {
  A: 0,
  B: 1,
  C: 3,
  D: 5,
  x: 0,
};

const l = input.length;

function getPoints(s: string) {
  const [a, b] = s.split("");
  const res = d[a] + d[b];
  if (a == "x" || b == "x") return res;
  return res + 2;
}

let res = 0;
let i = 0;

while (i < l - 1) {
  const e = input[i] + input[i + 1];
  console.log(e);

  const p = getPoints(e);
  console.log(p);
  res += p;
  i += 2;
}

console.log(res);
